    def __init__(self):
        self.items = []
        self.size = 0

    def isEmpty(self):
        return self.size == 0

    def push(self, element):
        self.items.append(element)
        self.size += 1

    def pop(self):
        if self.isEmpty():
            return None
        return self.items.pop()

    def size(self):
        return self.size

